import React, {useEffect} from "react";
import { connect } from "react-redux";
import {Tag} from "antd";
import "../static/style/footer.scss"
import {COUNTER, FETCH_PARAM} from "../constants/actionTypes";
const linkArr = ['极客网', '技能认证', '开放上机', '网址大全'];
const Footer = (props) => {
    const { handleCounter, counter, handleParam, pv, uv } = props;
    useEffect(() => {
        handleCounter();
        handleParam()
    },[]);
    return (
        <div className={"footerWrap"}>
            <div className={"footer"}>
                <div className={"footerTop"}>
                    <div>
                        <p>相关链接</p>
                        {
                            linkArr.map((item)=>(
                                <Tag
                                    key={item}
                                >
                                    <a>
                                        {item}
                                    </a>
                                </Tag>
                            ))
                        }
                    </div>
                </div>
                <div className={"footerBottom"}>
                    <p>版权所有 极客工作室</p>
                    <p style={{marginLeft: "1rem"}}>页面刷新量：{pv}</p>
                    <p style={{marginLeft: "1rem"}}>用户登录次数：{uv}</p>
                </div>
            </div>
        </div>
    )
};

export default connect(
    state => ({
        counter: state.getIn(["userMana", "common", "counter"]),
        pv: state.getIn(["userMana", "common", "pv"]),
        uv: state.getIn(["userMana", "common", "uv"]),
    }),
    dispatch => ({
        handleCounter() {
            dispatch({type: COUNTER});
        },
        handleParam() {
            dispatch({type: FETCH_PARAM})
        }
    })
)(Footer);
